import React from 'react';
// import ReactDOM from 'react-dom'; // FOR REACT 17
import ReactDOM from 'react-dom/client';

// create your app component, start with UPPERCASE
function App() {
	return <h1>Hello React!</h1>;
}

const Pizza = () => {
	return (
		<>
			<h2>Pizza</h2>
			<Pizza />
		</>
	);
};

// REACT 18 approach...
