//import react and reactdom
import React from 'react';
// import ReactDOM from 'react-dom'; // FOR REACT 17
import ReactDOM from 'react-dom/client';
import './index.css';

const pizzaData = [
	{
		name: 'Focaccia',
		ingredients: 'Bread with italian olive oil and rosemary',
		price: 6,
		photoName: 'pizzas/focaccia.jpg',
		soldOut: false,
	},
	{
		name: 'Pizza Margherita',
		ingredients: 'Tomato and mozarella',
		price: 10,
		photoName: 'pizzas/margherita.jpg',
		soldOut: false,
	},
	{
		name: 'Pizza Spinaci',
		ingredients: 'Tomato, mozarella, spinach, and ricotta cheese',
		price: 12,
		photoName: 'pizzas/spinaci.jpg',
		soldOut: false,
	},
	{
		name: 'Pizza Funghi',
		ingredients: 'Tomato, mozarella, mushrooms, and onion',
		price: 12,
		photoName: 'pizzas/funghi.jpg',
		soldOut: false,
	},
	{
		name: 'Pizza Salamino',
		ingredients: 'Tomato, mozarella, and pepperoni',
		price: 15,
		photoName: 'pizzas/salamino.jpg',
		soldOut: true,
	},
	{
		name: 'Pizza Prosciutto',
		ingredients: 'Tomato, mozarella, ham, aragula, and burrata cheese',
		price: 18,
		photoName: 'pizzas/prosciutto.jpg',
		soldOut: false,
	},
];

// create your app component, start with UPPERCASE
function App() {
	return (
		<div className={'container'}>
			<Header />
			<Menu />
			<Footer />
		</div>
	);
}

const Header = () => {
	return (
		<header className={'header'}>
			<h1>Fast React Pizza Co.</h1>
		</header>
	);
};

const Menu = () => {
	return (
		<div>
			<h2>Our Menu</h2>
			<Pizza />
			<Pizza />
			<Pizza />
		</div>
	);
};
const Footer = () => {
	const hour = new Date().getHours;
	const openHour = 12;
	const closeHour = 22;
	const isOpen = hour >= openHour && hour <= closeHour;
	console.log(isOpen);

	// if (hour >= openHour && hour >= closeHour) alert("We're currently open !");
	// else alert("Sorry, we're closed");

	return <footer>{new Date().toLocaleTimeString()}. We're currently open</footer>;
};

const Pizza = () => {
	return (
		<div>
			<img src='pizzas/spinaci.jpg' alt='Pizza Spinaci' />
			<h2>Pizza Spinaci</h2>
			<p>Tomato, mozarella, spinach, and ricotta cheese</p>
		</div>
	);
};

// REACT 18 approach...
// Specifiying an html element to be the element that your app will be, like #root
const root = ReactDOM.createRoot(document.getElementById('root'));
// render the app to the DOM
root.render(
	// A react component that renders our components twice to pick up bugs and will check if we are using outdated parts of React API.
	// Useful to do this DURING DEVELOPMENT
	<React.StrictMode>
		<App />
	</React.StrictMode>,
);

// REACT 17 approach...
// React.render(<App />), document.getElementById('root');
